    private void HitungLingkaranActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_HitungLingkaranActionPerformed
        // TODO add your handling code here:
        double jarijari;
        double luas;
        double keliling;
        
        jarijari=Double.parseDouble(textjarijari.getText());
        luas =  Math.PI * jarijari * jarijari ;
        keliling = Math.PI * 2 * jarijari;
        textluaslingkaran.setText(String.valueOf(luas));
        textkelilinglingkaran.setText(String.valueOf(keliling));
        
    }//GEN-LAST:event_HitungLingkaranActionPerformed

    private void hitungsegitigaActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_hitungsegitigaActionPerformed
        // TODO add your handling code here:
        double alas;
        double tinggi;
        double luas;
        double keliling;
        double sisimiring;
        
        alas=Double.parseDouble(textalas.getText());
        tinggi=Double.parseDouble(texttinggi.getText());
        luas=0.5 * alas * tinggi;
        sisimiring = Math.sqrt((alas*alas) + (tinggi*tinggi));
        keliling = alas + tinggi + sisimiring;
        textluas.setText(String.valueOf(luas));
        textsisimiring.setText(String.valueOf(sisimiring));
        textkeliling.setText(String.valueOf(keliling));
    }//GEN-LAST:event_hitungsegitigaActionPerformed

