    private void hitungpersegipanjnagActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_hitungpersegipanjnagActionPerformed
        // TODO add your handling code here:
        double panjang;
        double lebar;
        double luas;
        double keliling;
        
        panjang = Double.parseDouble(textpanjang.getText());
        lebar = Double.parseDouble(textlebar.getText());
        luas = panjang * lebar;
        keliling = 2 * (panjang + lebar);
        textluasPP.setText(String.valueOf(luas));
        textkelilingPP.setText(String.valueOf(keliling));
    }//GEN-LAST:event_hitungpersegipanjnagActionPerformed

    private void HitungPersegiActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_HitungPersegiActionPerformed
        // TODO add your handling code here:
        double sisi;
        double luas;
        double keliling;
        
        sisi=Double.parseDouble(textsisi.getText());
        luas = sisi * sisi;
        keliling = sisi * 4;
        textluaspersegi.setText(String.valueOf(luas));
        textkelilingpersegi.setText(String.valueOf(keliling));
    }//GEN-LAST:event_HitungPersegiActionPerformed


